attachInfoWindows=function(map,marker,place){var contentString='<div id="content"><div id="siteNotice"></div><h1 id="firstHeading" class="firstHeading">'+place.title+'</h1><div id="bodyContent"><p>'+place.text+'</p><p><a href="'+place.url+'"></a></p></div></div>',infowindow=new google.maps.InfoWindow({content:contentString});marker.addListener("click",function(){infowindow.open(map,marker)})};